CREATE TABLE `browser_statistic` (
  `date` date NOT NULL,
  `chrome` decimal(10,2) NOT NULL,
  `ie` decimal(10,2) NOT NULL,
  `firefox` decimal(10,2) NOT NULL,
  `safari` decimal(10,2) NOT NULL,
  `opera` decimal(10,2) NOT NULL,
  `android` decimal(10,2) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

	
